import React, { useState } from "react";
import axios from "axios";

const AppointmentForm = ({ setAppointments }) => {
  const [doctorId, setDoctorId] = useState("");
  const [patientId, setPatientId] = useState("");
  const [appointmentDate, setAppointmentDate] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const appointment = { patientId, doctorId, appointmentDate };
    const res = await axios.post("http://localhost:5000/api/appointments", appointment);
    setAppointments((prevAppointments) => [...prevAppointments, res.data]);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="ID do paciente"
        value={patientId}
        onChange={(e) => setPatientId(e.target.value)}
      />
      <input
        type="text"
        placeholder="ID do mÃ©dico"
        value={doctorId}
        onChange={(e) => setDoctorId(e.target.value)}
      />
      <input
        type="datetime-local"
        value={appointmentDate}
        onChange={(e) => setAppointmentDate(e.target.value)}
      />
      <button type="submit">Agendar Consulta</button>
    </form>
  );
};

export default AppointmentForm;
